<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class Eventmodel extends MY_Model {	var $title = '';	var $description = '';	var $locationAddr1 = '';	var $locationAddr2 = '';	var $city = '';	var $zip = '' ;	var $date = '' ;	    function __construct()  {     	parent::__construct();     	$date = date('Y-m-d');    }        public function getTableName(){ return 'event'; }    protected function fetchFromInput(){        parent::fetchFromInput();               $this->title = $this->input->post('title');        $this->description = $this->input->post('description');        $this->locationAddr1 = $this->input->post('locationAddr1');        $this->locationAddr2 = $this->input->post('locationAddr2');        $this->city = $this->input->post('city');        $this->zip = $this->input->post('zip');        $this->date = date('Y-m-d', strtotime($this->input->post('date')));    }    public function setResultSetObject($rs){    	if($rs == null)            return;    	parent::setResultSetObject($rs);    	$this->title = $rs->title;    	$this->description = $rs->description;    	$this->locationAddr1 = $rs->locationAddr1;    	$this->locationAddr2 = $rs->locationAddr2;    	$this->city = $rs->city;    	$this->zip = $rs->zip;    	$this->date = $rs->date;    }    public function getUiObject($rs){    	$input = parent::getUiObject($rs);		    	$inp =  [    			'3' =>['type'=>'hidden', 'label' => 'UniqueCode',	'fielddata'=>['name' => 'uniqueCode', 'class'=>'form-control',	'id' => 'uniqueCode', 	'value' => $this->uniqueCode,]],    			'4' =>['type'=>'textfield', 'label' => 'Title',	'fielddata'=>['name' => 'title', 	'class'=>'form-control',	'id' => 'title', 		'value' =>$this->title,]],    			'5' =>['type'=>'textarea', 'label' => 'Description',	'fielddata'=>['name' => 'description', 'class'=>'form-control',	'id' => 'description',	'value' => $this->description, 'rows'=>1]],    			'6' =>['type'=>'textfield', 'label' => 'Location Addr1',	'fielddata'=>['name' => 'locationAddr1', 'class'=>'form-control',	'id' => 'locationAddr1', 	'value' => $this->locationAddr1,]],    			'7' =>['type'=>'textfield', 'label' => 'Location Addr2',	'fielddata'=>['name' => 'locationAddr2', 'class'=>'form-control',	'id' => 'locationAddr2', 	'value' => $this->locationAddr2,]],    			'8' =>['type'=>'textfield', 'label' => 'City',	'fielddata'=>['name' => 'city', 'class'=>'form-control',	'id' => 'city', 	'value' => $this->city,]],    			'9' =>['type'=>'textfield', 'label' => 'Zip',	'fielddata'=>['name' => 'zip', 	'class'=>'form-control',	'id' => 'zip',			'value' => $this->zip, ]],    			'10' =>['type'=>'textfield', 'label' => 'Date',	'fielddata'=>['name' => 'date', 	'class'=>'form-control dtpkr',	'id' => 'date',			'value' => date('m/d/Y', strtotime($this->date)), ]],    		];    	foreach ($inp as $key => $value) {    		$input[$key] = $value;    	}    	    	return $input;    }    }?>