<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');
class Rolemodel extends MY_Model {	    	var $description = '' ;    	    	function __construct()  {    		parent::__construct();    	}    	    	public function getTableName(){ return 'roles'; }    	    	protected function fetchFromInput(){    		parent::fetchFromInput();    		    		$this->description = $this->input->post('description');    	}    	    	public function setResultSetObject($rs){    		if($rs == null)    			return;    			parent::setResultSetObject($rs);    			$this->description = $rs->description;    	}    	    	public function getUiObject($rs){    		$input = parent::getUiObject($rs);    		    		$inp =  [    				'3' =>['type'=>'textfield', 'label' => 'Role',	'fielddata'=>['name' => 'uniqueCode', 'class'=>'form-control',	'id' => 'uniqueCode', 	'value' => $this->uniqueCode,]],    				'4' =>['type'=>'textfield', 'label' => 'Description',	'fielddata'=>['name' => 'description', 	'class'=>'form-control',	'id' => 'description', 		'value' => $this->description,]],		    		];    		    		foreach ($inp as $key => $value) {    			$input[$key] = $value;    		}    		    		return $input;    	}    }
?>