<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class Usermodel extends MY_Model {	var $firstName = '';	var $lastName = '';	var $password = '';	var $email = '';	var $contactNo = '';	var $type = '' ;    function __construct()  {     	parent::__construct();     	$this->type = 'ADMIN';    }        public function getTableName(){ return 'user'; }    protected function fetchFromInput(){        parent::fetchFromInput();               $this->firstName = $this->input->post('firstName');        $this->lastName = $this->input->post('lastName');        $this->password = $this->input->post('password');        $this->email = $this->input->post('email');        $this->contactNo = $this->input->post('contactNo');        $this->type = $this->input->post('type');    }    public function setResultSetObject($rs){    	if($rs == null)            return;    	parent::setResultSetObject($rs);    	$this->firstName = $rs->firstName;    	$this->lastName = $rs->lastName;    	$this->password = $rs->password;    	$this->email = $rs->email;    	$this->contactNo = $rs->contactNo;    	$this->type = $rs->type;    }    public function getUiObject($rs){    	$input = parent::getUiObject($rs);		    	$inp =  [    			'3' =>['type'=>'textfield', 'label' => 'Username',	'fielddata'=>['name' => 'uniqueCode', 'class'=>'form-control',	'id' => 'uniqueCode', 	'value' => $this->uniqueCode,]],    			'4' =>['type'=>'password', 'label' => 'Password',	'fielddata'=>['name' => 'password', 	'class'=>'form-control',	'id' => 'password', 		'value' => '',]],    			'5' =>['type'=>'textfield', 'label' => 'First Name',	'fielddata'=>['name' => 'firstName', 'class'=>'form-control',	'id' => 'firstName',	'value' => $this->firstName,]],    			'6' =>['type'=>'textfield', 'label' => 'Last Name',	'fielddata'=>['name' => 'lastName', 'class'=>'form-control',	'id' => 'lastName', 	'value' => $this->lastName,]],    			'7' =>['type'=>'textfield', 'label' => 'Email',	'fielddata'=>['name' => 'email', 'class'=>'form-control',	'id' => 'email', 	'value' => $this->email,]],    			'8' =>['type'=>'textfield', 'label' => 'Phone',	'fielddata'=>['name' => 'contactNo', 'class'=>'form-control',	'id' => 'contactNo', 	'value' => $this->contactNo,]],    			'9' =>['type'=>'dropdown', 'label' => 'Type',	'fielddata'=>['name' => 'type', 	'class'=>'form-control',	'id' => 'type',			'value' => $this->type, 'options'=>['admin'=>'Admin', 'partner'=>'Partner', 'customer'=>'Customer']]],    		];    	foreach ($inp as $key => $value) {    		$input[$key] = $value;    	}    	    	return $input;    }    }?>